(31, 1486204958, 'new', 'dnsChange', 2, 'c1f94b58f4b921454888faa55438db84', 'c1f94b58f4b921454888faa55438db84', 'Your DNS records have changed', 'We have detected a change in the A records of your DNS configuration that may affect the domain www.nsamr.ac.uk. An A record is a record in DNS that points a domain name to an IP address. A change in your DNS records may indicate that a hacker has hacked into your DNS administration system and has pointed your email or website to their own server for malicious purposes. It could also indicate that your domain has expired. If you made this change yourself you can mark it \'resolved\' and safely ignore it.', 'a:4:{s:4:"type";s:1:"A";s:4:"host";s:15:"www.nsamr.ac.uk";s:6:"oldDNS";s:38:"www.nsamr.ac.uk points to 93.93.131.30";s:6:"newDNS";s:40:"www.nsamr.ac.uk points to 95.142.152.198";}'),
(32, 1486204961, 'new', 'wfUpgrade', 1, 'df0084d80d1acf38c5505ba251c2d9e5', 'df0084d80d1acf38c5505ba251c2d9e5', 'Your WordPress version is out of date', 'WordPress version 4.7.2 is now available. Please upgrade immediately to get the latest security updates from WordPress.', 'a:2:{s:14:"currentVersion";s:5:"4.7.1";s:10:"newVersion";s:5:"4.7.2";}'),
